DROP TABLE IF EXISTS `objectboard_db`.`Profiles`;
DROP VIEW IF EXISTS `objectboard_db`.`Profiles` ;
USE `objectboard_db`;
CREATE  OR REPLACE VIEW `Profiles` AS
    SELECT 
        M.*,
        P.co_name AS contry_name,
        S.st_name AS state_name,
        C.ci_name AS city_name,
        B.bu_name AS bu_name
    FROM
        objectboard_db.masterUser M,
        objectboard_db.country P,
        objectboard_db.states S,
        objectboard_db.city C,
        objectboard_db.bussinessUnit B
    WHERE
        (M.city_states_country_co_country_code = P.co_country_code
            AND M.city_states_st_state_code = S.st_state_code
            AND M.city_ci_city_code = C.ci_city_code
            AND M.bussinessUnit_bu_bis_code = B.bu_bis_code);

