DROP TABLE IF EXISTS `objectboard_db`.`SurveyTotalView`;
DROP VIEW IF EXISTS `objectboard_db`.`SurveyTotalView` ;
USE `objectboard_db`;
CREATE  OR REPLACE VIEW `SurveyTotalView` AS
SELECT HS.survey_code,
HS.bussinessUnit_bu_bis_code	AS bussinessUnit_code,
HS.hs_name AS survey_name,
HS.hs_references,
HS.total_questions,
HS.surveyStatus,
HS.execution_status,
HS.version,
HS.date_creation,
HS.typified_ctypified_code1,
HS.typified_ctypified_code2,
HS.typified_ctypified_code3,
HS.organization_level1,
HS.organization_level2,
HS.organization_level3,
HS.organization_level4,
HS.date_last_modification,
HS.audited,
BQ.question_code,
BQ.type_request,
BQ.main_request,
BQ.Annex_type,
BQ.body_annex_doc,
BQ.body_annex_photo,
BQ.comment,
BQ.answer_number,
BQ.solution_number,
BQ.auditable_solution AS auditable_question_solution,
BQ.auditable_answer_solution AS auditable_inquestion_answer_solution,
BA.answer_code,
BA.answer,
BA.answer_solution,
BA.auditable_solution AS auditable_answer_solution,
BA.answer_only_text,
BA.answer_only_number,
BA.answer_only_date,
BA.answer_only_time
FROM objectboard_db.headersSurvey HS, 
     objectboard_db.bodySurveyQuestions BQ,
     objectboard_db.bodySurveyAnswers BA
WHERE ((BQ.headersSurvey_survey_code = HS.survey_code AND 
       BQ.headersSurvey_bussinessUnit_bu_bis_code = HS.bussinessUnit_bu_bis_code) AND
       (BA.bodySurveyQuestions_question_code = BQ.question_code AND
        BA.bodySurveyQuestions_headersSurvey_survey_code = BQ.headersSurvey_survey_code AND
        BA.bodySurveyQuestions_headersSurvey_bussinessUnit_bu_bis_code = BQ.headersSurvey_bussinessUnit_bu_bis_code));

